symbols.signature:
export function extractFileSymbols(
  sourceFile: SourceFile,
  projectRoot: string
): SymbolDoc[]

export async function generateDocIndex(
  options: IndexerOptions
): Promise<DocIndex>

export interface IndexerOptions {
  tsConfigFilePath: string;
  projectRoot: string;
  outputPath?: string;
}

export interface ExtractedJsDoc {
  description?: string;
  params?: ExtractedParam[];
  returns?: { type?: string; description?: string };
  tags?: Record<string, string>;
}

export interface ExtractedParam {
  name: string;
  type?: string;
  description?: string;
}

export function getSymbol(
  index: DocIndex,
  id: string
): SymbolDoc | undefined

export function listSymbols(
  index: DocIndex
): Array<{ id: string; kind: string; name: string }>

export function searchSymbols(
  index: DocIndex,
  query: string,
  limit?: number
): SymbolDoc[]

export function getSignature(
  declaration: ExportedDeclarations
): string | undefined

export interface BuildController {
  scheduleRebuild(): void;
  waitForIdle(): Promise<void>;
  getState(): "idle" | "building" | "build_queued";
}

export function createBuildController(
  buildFn: () => Promise<void>,
  debounceMs: number
): BuildController

export async function watchAndRegenerate(
  options: WatcherOptions
): Promise<WatcherHandle>

export interface WatcherHandle {
  close(): Promise<void>;
  watcher: FSWatcher;
}

export interface WatcherOptions extends IndexerOptions {
  debounceMs?: number;
  watchPaths?: string[];
  onBuildStart?: () => void;
  onBuildComplete?: (symbolCount: number) => void;
  onBuildError?: (error: unknown) => void;
}
